DROP TABLE IF EXISTS "hpfilminglocations" CASCADE;

CREATE TABLE "hpfilminglocations" ( "ogc_fid" SERIAL, CONSTRAINT "hpfilminglocations_pk" PRIMARY KEY ("ogc_fid"),
"wkb_geometry" GEOMETRY DEFAULT ST_GeomFromText('POINT(-6.4 57.6)', 4326),
"locationname" VARCHAR,
"country" VARCHAR,
"address" VARCHAR,
"movie" VARCHAR
 );

CREATE INDEX "hpfilminglocations_wkb_geometry_geom_idx" ON "hpfilminglocations" USING GIST ("wkb_geometry");



